    const { setOpen } = useWalletDialog();
    const handleClick = useCallback((event) => {
        if (onClick)
            onClick(event);
        if (!event.defaultPrevented)
            setOpen(true);
    }, [onClick, setOpen]);
    return (React.createElement(Button, { color: color, variant: variant, type: type, onClick: handleClick, ...props }, children));
